    $.fn.tabs_textTabs = function(options){

        var create_work_Tabs = function(){
            var tabs = this;
            var i = 0;
            
            var showPage = function(i){
                $(tabs).children(".faq-content").children(".item").hide();
                $(tabs).children(".faq-content").children(".item").eq(i).show();			
                $(tabs).find(".tabs-faq__sidebar__list").children("li").removeClass("active");
                $(tabs).find(".tabs-faq__sidebar__list").children("li").eq(i).addClass("active");
            }
                                
            showPage(0);				
            
            $(tabs).find(".tabs-faq__sidebar__list").children("li").each(function(index, element){
                $(element).attr("data-page", i);
                i++;                        
            });
            
            $(tabs).find(".tabs-faq__sidebar__list").children("li").click(function(){
                showPage(parseInt($(this).attr("data-page")));
            });				
        };		
        return this.each(create_work_Tabs);
    };

    $(".faq-content .item-box .title").on("click", function() {
        if ($(this).hasClass("active")) {
          $(this).removeClass("active");
          $(this)
            .siblings(".text")
            .slideUp(200);
        } else {
          $(".faq-content .item-box .title").removeClass("active");
          $(this).addClass("active");
          $(".text").slideUp(200);
          $(this)
            .siblings(".text")
            .slideDown(200);
        }
      });

    $(".tabs-faq").tabs_textTabs();

